import java.sql.{Connection, PreparedStatement, ResultSet, SQLException}
import scala.util.{Try, Success, Failure}

/**
 * SQLite数据库工具类
 * 提供常用的数据库操作功能，包括表创建、数据插入、查询等
 */
object SqliteUtils {

 /**
 * 执行DDL语句（如CREATE TABLE）
 * @param dbPath 数据库文件路径
 * @param sql DDL语句
 * @return 执行结果
 */
 def executeDDL(dbPath: String, sql: String): Try[Unit] = {
 var conn: Connection = null
 var stmt: PreparedStatement = null

 try {
 conn = SqliteConnectionPool.getConn(dbPath)
 stmt = conn.prepareStatement(sql)
 stmt.execute()
 Success(())
 } catch {
 case e: SQLException =>
 println(s"执行DDL失败: ${e.getMessage}")
 Failure(e)
 } finally {
 if (stmt != null) stmt.close()
 if (conn != null) SqliteConnectionPool.returnConn(conn)
 }
 }

 /**
 * 执行DML语句（如INSERT, UPDATE, DELETE）
 * @param dbPath 数据库文件路径
 * @param sql DML语句
 * @param params 参数列表
 * @return 影响的行数
 */
 def executeDML(dbPath: String, sql: String, params: Any*): Try[Int] = {
 var conn: Connection = null
 var stmt: PreparedStatement = null

 try {
 conn = SqliteConnectionPool.getConn(dbPath)
 stmt = conn.prepareStatement(sql)

 // 设置参数
 params.zipWithIndex.foreach { case (param, index) =>
 param match {
 case s: String => stmt.setString(index + 1, s)
 case i: Int => stmt.setInt(index + 1, i)
 case l: Long => stmt.setLong(index + 1, l)
 case d: Double => stmt.setDouble(index + 1, d)
 case f: Float => stmt.setFloat(index + 1, f)
 case b: Boolean => stmt.setBoolean(index + 1, b)
 case null => stmt.setNull(index + 1, java.sql.Types.NULL)
 case _ => stmt.setString(index + 1, param.toString)
 }
 }

 val result = stmt.executeUpdate()
 Success(result)
 } catch {
 case e: SQLException =>
 println(s"执行DML失败: ${e.getMessage}")
 Failure(e)
 } finally {
 if (stmt != null) stmt.close()
 if (conn != null) SqliteConnectionPool.returnConn(conn)
 }
 }

 /**
 * 执行查询语句
 * @param dbPath 数据库文件路径
 * @param sql 查询语句
 * @param params 参数列表
 * @param processor 结果处理函数
 * @return 查询结果
 */
 def executeQuery[T](dbPath: String, sql: String, params: Any*)(processor: ResultSet => T): Try[T] = {
 var conn: Connection = null
 var stmt: PreparedStatement = null
 var rs: ResultSet = null

 try {
 conn = SqliteConnectionPool.getConn(dbPath)
 stmt = conn.prepareStatement(sql)

 // 设置参数
 params.zipWithIndex.foreach { case (param, index) =>
 param match {
 case s: String => stmt.setString(index + 1, s)
 case i: Int => stmt.setInt(index + 1, i)
 case l: Long => stmt.setLong(index + 1, l)
 case d: Double => stmt.setDouble(index + 1, d)
 case f: Float => stmt.setFloat(index + 1, f)
 case b: Boolean => stmt.setBoolean(index + 1, b)
 case null => stmt.setNull(index + 1, java.sql.Types.NULL)
 case _ => stmt.setString(index + 1, param.toString)
 }
 }

 rs = stmt.executeQuery()
 val result = processor(rs)
 Success(result)
 } catch {
 case e: SQLException =>
 println(s"执行查询失败: ${e.getMessage}")
 Failure(e)
 } finally {
 if (rs != null) rs.close()
 if (stmt != null) stmt.close()
 if (conn != null) SqliteConnectionPool.returnConn(conn)
 }
 }

 /**
 * 批量执行DML语句
 * @param dbPath 数据库文件路径
 * @param sql DML语句
 * @param paramsList 参数列表的列表
 * @return 每个语句影响的行数数组
 */
 def executeBatch(dbPath: String, sql: String, paramsList: List[List[Any]]): Try[Array[Int]] = {
 var conn: Connection = null
 var stmt: PreparedStatement = null

 try {
 conn = SqliteConnectionPool.getConn(dbPath)
 conn.setAutoCommit(false) // 开启事务
 stmt = conn.prepareStatement(sql)

 paramsList.foreach { params =>
 params.zipWithIndex.foreach { case (param, index) =>
 param match {
 case s: String => stmt.setString(index + 1, s)
 case i: Int => stmt.setInt(index + 1, i)
 case l: Long => stmt.setLong(index + 1, l)
 case d: Double => stmt.setDouble(index + 1, d)
 case f: Float => stmt.setFloat(index + 1, f)
 case b: Boolean => stmt.setBoolean(index + 1, b)
 case null => stmt.setNull(index + 1, java.sql.Types.NULL)
 case _ => stmt.setString(index + 1, param.toString)
 }
 }
 stmt.addBatch()
 }

 val result = stmt.executeBatch()
 conn.commit() // 提交事务
 Success(result)
 } catch {
 case e: SQLException =>
 if (conn != null) {
 try {
 conn.rollback() // 回滚事务
 } catch {
 case _: SQLException => // 忽略回滚异常
 }
 }
 println(s"批量执行失败: ${e.getMessage}")
 Failure(e)
 } finally {
 if (conn != null) {
 try {
 conn.setAutoCommit(true) // 恢复自动提交
 } catch {
 case _: SQLException => // 忽略异常
 }
 }
 if (stmt != null) stmt.close()
 if (conn != null) SqliteConnectionPool.returnConn(conn)
 }
 }

